					[ 
							'book_name' => 'Tpot Collection 05 - An Yên', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: medium; color: #ff6600;&quot;&gt;&lt;strong&gt;T&middot;pot Collection 05 - An Y&ecirc;n&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Ấn phẩm &lt;strong&gt;T&middot;pot Collection 05: Home sweet home (An y&ecirc;n) &lt;/strong&gt;ph&aacute;t h&agrave;nh m&ugrave;a Thu n&agrave;y sẽ đem lại những c&acirc;u chuyện &quot;Nh&agrave;&quot; &yacute; nghĩa. Đồng thời đ&oacute; cũng l&agrave; tuyển tập những địa chỉ homestay xinh xắn, hay những ng&ocirc;i nh&agrave; c&oacute; thiết kế độc đ&aacute;o để bạn được truyền cảm hứng cải tạo kh&ocirc;ng gian sống của m&igrave;nh. Với số lượng trang tăng l&ecirc;n v&agrave; chất lượng in ấn tốt hơn hẳn c&aacute;c số trước, mong rằng bạn sẽ t&igrave;m thấy những ph&uacute;t gi&acirc;y an y&ecirc;n khi đọc s&aacute;ch.&lt;/p&gt;  &lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; title=&quot;T&middot;pot Collection 05 - An Y&ecirc;n&quot; src=&quot;https://tikicdn.com/media/catalog/product/h/i/hinh%20minh%20hoa.u547.d20160824.t115921.981638.jpg&quot; alt=&quot;T&middot;pot Collection 05 - An Y&ecirc;n&quot; width=&quot;636&quot; height=&quot;450&quot; /&gt;&lt;/p&gt;'), 
