							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;T&igrave;nh Dục Ư? Chuyện Nhỏ&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&ldquo;Nếu t&igrave;nh dục mu&ocirc;n đời mu&ocirc;n kiếp l&agrave; chuyện nhỏ th&igrave; c&aacute;c nhạc sĩ, nh&agrave; văn, nh&agrave; thơ&hellip; đ&atilde; bu&ocirc;ng b&uacute;t t&ocirc; vẽ v&agrave; t&ocirc;n vinh t&igrave;nh dục. Hẳn thế giới n&agrave;y sẽ trở n&ecirc;n trống rỗng, tẻ nhạt v&agrave; mất kiểm so&aacute;t khi người đ&agrave;n b&agrave; dạn dĩ cởi hết v&aacute;y &aacute;o, xi&ecirc;m y m&agrave; chẳng ch&uacute;t thẹn th&ugrave;ng, người đ&agrave;n &ocirc;ng sẽ chẳng c&ograve;n phải tốn c&ocirc;ng tốn sức &ocirc;m hoa hồng quỳ gối trước người đẹp để ước mong một nụ h&ocirc;n hay những đ&ecirc;m nồng ch&aacute;y b&ecirc;n n&agrave;ng.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Thế c&oacute; buồn kh&ocirc;ng?&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Bởi t&igrave;nh dục l&agrave; chuyện nhỏ th&ocirc;i m&agrave;!&rdquo;&lt;/p&gt;'), 
