							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: medium; color: #ff6600;&quot;&gt;&lt;strong&gt;&lt;a href=&quot;http://tiki.vn/conan-tham-tu-lung-danh/c1087&quot;&gt;Th&aacute;m Tử Lừng Danh Conan&lt;/a&gt; - Tuyển Tập Đặc Biệt: Những C&acirc;u Chuyện L&atilde;ng Mạn - Tập 1&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: medium; color: #ff6600;&quot;&gt;&lt;span style=&quot;font-size: small; color: #000000;&quot;&gt;Cuốn s&aacute;ch n&agrave;y tập hợp những mẩu chuyện l&atilde;ng mạn giữa Conan (Shinichi) &lt;/span&gt;&lt;span style=&quot;font-size: small; color: #000000;&quot;&gt;v&agrave; Ran. Chuyện t&igrave;nh giữa Conan (Shinichi) v&agrave; Ran khiến độc giả kh&ocirc;ng sao rời mắt được... T&igrave;nh cảm giữa họ tiến triển từng bước, như mưa dầm thấm l&acirc;u...!?&lt;/span&gt;&lt;strong&gt;&lt;br /&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;'), 
