							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;Từ Điển Hoa - Việt Ph&acirc;n Loại Bằng H&igrave;nh Gấu Tr&uacute;c (Tập 2)&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;strong&gt;Từ Điển Hoa Việt - Ph&acirc;n Loại Bằng H&igrave;nh - Gấu Tr&uacute;c (Tập 2)&lt;/strong&gt; được bi&ecirc;n soạn ri&ecirc;ng cho dụng cụ bút th&ocirc;ng minh. &lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Sách chọn lọc những từ vựng đơn giản, th&ocirc;ng dụng, thường gặp trong cuộc sống hằng ng&agrave;y. Đặc biệt, b&eacute; sẽ d&ecirc;̃ nhớ hơn do c&oacute; những hình minh hoạ đ&acirc;̀y màu sắc và sinh đ&ocirc;̣ng, ph&ugrave; hợp với ngữ cảnh. Chắc chắn với sản ph&acirc;̉m này, các bé sẽ có bước đ&acirc;̀u học ti&ecirc;́ng Hoa th&acirc;̣t lý thú c&ugrave;ng bạn gấu tr&uacute;c, tạo cho bé ni&ecirc;̀m say m&ecirc; với ng&ocirc;n ngữ n&agrave;y.&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Cuốn s&aacute;ch&nbsp;&lt;span&gt;&lt;strong&gt;Từ Điển Hoa Việt - Ph&acirc;n Loại Bằng H&igrave;nh - Gấu Tr&uacute;c (Tập 2)&lt;/strong&gt; bao gồm 11 b&agrave;i học:&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;1. C&aacute;c sự kiện trong cuộc đời ch&uacute;ng ta&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;2. Tổ chức sinh nhật&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;3. H&ocirc;n nh&acirc;n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;4. C&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;5. Nghề nghiệp&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;6. Văn ph&ograve;ng l&agrave;m việc&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;7. Ch&agrave;o hỏi&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;8. Quần &aacute;o&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;9. Giầy n&oacute;n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;10. Phụ kiện&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;11. &lt;a href=&quot;http://tiki.vn/trang-diem/c1584&quot;&gt;Trang điểm&lt;/a&gt;.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&nbsp;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;'), 
