					[ 
							'book_name' => 'Sống Theo Phương Thức 80/20 (Tái Bản 2011)', 
							'description' => html_entity_decode('&lt;p&gt;&lt;span style=&quot;font-size: medium; color: #008000;&quot;&gt;&lt;strong&gt;Sống Theo Phương Thức 80/20 (T&aacute;i Bản 2011)&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p&gt;Ở quyển s&aacute;ch n&agrave;y, t&aacute;c giả của nguy&ecirc;n l&yacute; 80/20 tập trung chỉ c&aacute;ch để th&agrave;nh c&ocirc;ng trong cuộc sống cũng như trong sự nghiệp. Theo &ocirc;ng, ch&igrave;a kh&oacute;a của sự th&agrave;nh c&ocirc;ng l&agrave; tập trung v&agrave;o chỉ v&agrave;i thứ thật sự quan trọng, v&agrave;i phương ph&aacute;p mang lại cho ch&uacute;ng ta điều ch&uacute;ng ta muốn, v&agrave; h&agrave;nh động dựa v&agrave;o đ&oacute;, bỏ đi những thứ vặt v&atilde;nh l&agrave;m ngăn cản cuộc sống.&lt;/p&gt;  &lt;p&gt;Phương c&aacute;ch 80/20 cho bạn thấy c&aacute;ch &aacute;p dụng những &yacute; tưởng của 80% v&agrave;o 20% tốt nhất của bạn để th&agrave;nh c&ocirc;ng về t&agrave;i ch&iacute;nh, mối quan hệ, c&oacute; cuộc sống tốt đẹp v&agrave; giản dị, v&agrave; sẽ gi&uacute;p bạn ph&aacute;t triển kế hoạch h&agrave;nh động c&aacute; nh&acirc;n để thay đổi cuộc sống.&lt;/p&gt;  &lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;Mời c&aacute;c bạn đ&oacute;n đọc.&lt;/strong&gt;&lt;/p&gt;'), 
