					[ 
							'book_name' => 'Sức Mạnh Của Trí Tuệ Tâm Linh', 
							'description' => html_entity_decode('&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;&lt;strong&gt;Sức Mạnh Của Tr&iacute; Tuệ T&acirc;m Linh&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p&gt;Tr&iacute; tuệ t&acirc;m linh sẽ soi r&otilde; tầm nh&igrave;n của bạn, mở ra trước mắt bạn cả một &quot;bức tranh to&agrave;n cảnh&quot; để bạn hiểu th&ecirc;m về bản th&acirc;n, cũng như những người xung quanh; cũng cố l&ograve;ng trắc ẩn; kh&aacute;m ph&aacute; những c&aacute;ch l&agrave;m tươi mới t&acirc;m hồn m&igrave;nh mỗi ng&agrave;y.&lt;/p&gt;'), 
