					[ 
							'book_name' => 'Nói Nhiều Không Bằng Nói Đúng', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;N&oacute;i Nhiều Kh&ocirc;ng Bằng N&oacute;i Đ&uacute;ng&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Quyển s&aacute;ch cung cấp 36 b&iacute; quyết&nbsp;để chiếm&nbsp;được cảm t&igrave;nh của người kh&aacute;c.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Muốn c&oacute; mối quan hệ x&atilde; hội tốt, h&atilde;y bắt&nbsp;đầu từ những lời n&oacute;i l&agrave;m&nbsp;đẹp l&ograve;ng mọi người. Trong giao tiếp, h&atilde;y lu&ocirc;n nhớ c&aacute;c nguy&ecirc;n t&aacute;c: Im lặng - Quan s&aacute;t - Lắng nghe.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Đừng n&oacute;i qu&aacute; thẳng thắn, khiến người nghe thấy kh&oacute; chịu!&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;N&oacute;i chuyện kh&ocirc;ng dễ nghe sẽ khiến mọi người phản cảm v&agrave; xa c&aacute;ch bạn,&nbsp;đồng thời dẫn&nbsp;đến việc bạn trở th&agrave;nh người c&oacute;&nbsp;ấn tượng xấu.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Lời n&oacute;i hay như những giai&nbsp;điệu&nbsp;đẹp, ai cũng muốn nghe.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Mời c&aacute;c bạn&nbsp;đ&oacute;n&nbsp;đọc&lt;/strong&gt;.&lt;/p&gt;'), 
