							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;Nổi Danh Vang Dội - Những Nh&agrave; Ph&aacute;t Minh V&agrave; C&aacute;c &Yacute; Tưởng Lạ Đời&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Những c&acirc;u chuyện th&uacute; vị về c&aacute;c nh&agrave; ph&aacute;t minh với những &yacute; tưởng lạ đời như Thomas Edison, John Logie Baird qua ng&ograve;i b&uacute;t cực kỳ vui nhộn của Dr Mike Goldsmith, đ&ocirc;i l&uacute;c những &yacute; tưởng bất chợt l&oacute;e s&aacute;ng tưởng chừng kh&ocirc;ng ăn nhập g&igrave; đến những ph&aacute;t minh của họ nhưng họ đ&atilde; nhanh ch&oacute;ng t&oacute;m lấy v&agrave; biến th&agrave;nh t&agrave;i sản gi&aacute; trị cho nh&acirc;n loại.&lt;/p&gt;'), 
