					[ 
							'book_name' => 'Minh Triết Trong Đời Sống (Tái Bản)', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: medium; color: #ff6600;&quot;&gt;&lt;strong&gt;Minh Triết Trong Đời Sống&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Trong thập ni&ecirc;n vừa qua, t&ocirc;i đ&atilde; diễn thuyết khắp nơi về nhiều đề t&agrave;i kh&aacute;c nhau như &quot;Con đường chuyển h&oacute;a&quot;, &quot;Định hướng cho tương lai&quot;, &quot;Hạnh ph&uacute;c v&agrave; đau khổ&quot;,... Th&iacute;nh giả của t&ocirc;i gồm đủ mọi hạng người trong x&atilde; hội, gi&aacute;m đốc x&iacute; nghiệp, quản l&yacute; thương mại, gi&aacute;o sư đại học, sinh vi&ecirc;n, học sinh, qu&acirc;n nh&acirc;n, c&ocirc;ng nh&acirc;n, thợ thuyền... Sau buổi n&oacute;i chuyện thường c&oacute; phần thảo luận, một số người đ&atilde; đặt những c&acirc;u hỏi tương tự như sau:&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;- Cuộc đời của t&ocirc;i l&agrave; một chuỗi đau khổ, b&agrave; c&oacute; c&aacute;ch n&agrave;o gi&uacute;p t&ocirc;i kh&ocirc;ng?&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;- Tại sao những bất hạnh lại xảy ra cho t&ocirc;i? T&ocirc;i đ&atilde; l&agrave;m g&igrave; n&ecirc;n tội?&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;- Tại sao l&uacute;c n&agrave;o t&ocirc;i cũng cảm thấy bất an, lo sợ?&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;- L&agrave;m c&aacute;ch n&agrave;o để một người b&igrave;nh thường như t&ocirc;i c&oacute; thể thoải m&aacute;i trong đời sống hiện tại?&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;...&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;T&ocirc;i quan niệm rằng: &quot;T&acirc;m hồn con người hiện nay đ&atilde; trở n&ecirc;n qu&aacute; m&aacute;y m&oacute;c, thụ động, kh&ocirc;ng thể tự chữa cho n&oacute; bằng c&aacute;c l&yacute; luận th&ocirc;ng thường. N&oacute; cần phải được n&acirc;ng l&ecirc;n một b&igrave;nh diện kh&aacute;c cao hơn để mở rộng ra, nh&igrave;n mọi sự qua một nh&atilde;n quang mới. Chỉ c&oacute; &aacute;p dụng c&aacute;ch đ&oacute; việc chữa trị mới mang lại kết quả tốt đẹp được&quot;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;&lt;span&gt;Mục lục:&lt;/span&gt;&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;Thần chết v&agrave; đời sống&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;T&iacute;nh n&oacute;ng giận&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Sự n&oacute;ng giận&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Quyền tức giận&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Sự gắn b&oacute;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Nghịch cảnh&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Mặc cảm tội lỗi&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;T&iacute;nh n&ocirc;n n&oacute;ng&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Ly nước đầy&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Kiềm chế, bộc lộ v&agrave; dứt bỏ&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;T&iacute;nh do dự&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Căn bệnh của tr&iacute; n&atilde;o&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Sự ganh tỵ&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Số nhiều&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Trong tinh thần thiền định&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Tư tưởng v&agrave; h&agrave;nh động&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Giải tho&aacute;t&lt;/span&gt;&lt;/p&