					[ 
							'book_name' => 'Mega Luyện Giải Đề Tổ Hợp Khoa Học Xã Hội 2017 Môn Sử - Địa - GDCD', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: medium; color: #ff6600;&quot;&gt;&lt;strong&gt;Mega Luyện Giải Đề Tổ Hợp Khoa Học X&atilde; Hội 2017 M&ocirc;n Sử - Địa - GDCD&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span id=&quot;docs-internal-guid-2a5315fe-62aa-2bed-e1ab-fab9a97b2c9b&quot;&gt; &lt;/span&gt;&lt;/p&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Bộ 7 cuốn Mega Luyện đề THPT Quốc gia 2017 c&oacute; những ưu điểm vượt trội:&lt;/span&gt;&lt;/p&gt;  &lt;ul&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;100% CHUẨN CẤU TR&Uacute;C đề thi &nbsp;2017 theo những thay đổi mới nhất của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo: Megabook lu&ocirc;n cập nhật nhanh nhất - kịp thời nhất - cho ra đời những cuốn s&aacute;ch tối ưu nhất!&lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Lộ tr&igrave;nh CHUẨN học theo năng lực gi&uacute;p học sinh trung b&igrave;nh kh&aacute; hay giỏi đều c&oacute; thể &ocirc;n tập đạt điểm 8 - 9 &lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Thử sức với bộ 20 - 40 đề thi thử chuẩn cấu tr&uacute;c thi 2017. MI&Ecirc;N PH&Iacute; 10 bộ đề thi thử Online tr&ecirc;n trang web Megatest - luyện thi &lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Gi&uacute;p em Th&agrave;nh Thạo Kĩ Năng giải đề THPT Quốc Gia 2017:&lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;/ul&gt;  &lt;ul&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Ghi nhớ kiến thức v&agrave; r&egrave;n kĩ năng l&agrave;m đề nhanh - ch&iacute;nh x&aacute;c.&lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;L&agrave;m quen với &aacute;p lực thời gian của từng đề thi - thi thử như thi thật - r&egrave;n học sinh kĩ năng l&agrave;m b&agrave;i dưới &aacute;p lực thời gian.&lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Lời giải định hướng tư duy, phương ph&aacute;p giải nhanh chỉ 1 ph&uacute;t cho 1 c&acirc;u, gi&uacute;p em c&acirc;n đối thời gian l&agrave;m b&agrave;i &lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;/ul&gt;  &lt;h1 dir=&quot;ltr&quot;&gt;&lt;span&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;https://lh3.googleusercontent.com/urgLqo12w5soC13HzytOcj0ChxPcKQXroO0uuv2DGXPJmgaatUFtaZJhjn9HD3NATETBVLC9_gOwn4PtCfctlGFY47MEDDcpFMMdjdhlrVDHcG8ORWFOhZFbulskUmPHC_KTvWIf&quot; alt=&quot;banner-tiki-01.jpg&quot; width=&quot;452&quot; height=&quot;301&quot; /&gt;&lt;/span&gt;&lt;/h1&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Trong trang s&aacute;ch của Mega c&oacute; điểm g&igrave; đặc biệt?&lt;/span&gt;&lt;/p&gt;  &lt;ul&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Lộ tr&igrave;nh học tập hiệu quả cho 99-er &lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;/ul&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;https://lh4.googleusercontent.com/GgQ32-80jw2rUWsCwBFpXcQVeobFxSybs3zK_JsTVm3PQSh8T3fFPmsIsR9ZCX7dRA1xE0ODv-g7L2DvJctv3c6sILWW8Yew2dlscTsie4ngHPJXFA9k-x8LtqiGBSxAUYslGRjp&quot; alt=&quot;2.png&quot; width=&quot;452&quot; height=&quot;301&quot; /&gt;&lt;/span&gt;&lt;/p&gt;  &lt;ul&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Lộ tr&igrave;nh cải thiện điểm số - con đường dẫn tới th&agrave;nh c&ocirc;ng.&lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Lời giải chi tiết cho mỗi bộ đề, tổng hợp l&yacute; thuyết: Lời giải kh&ocirc;ng đơn thuần chỉ c&oacute; đ&aacute;p &aacute;n Đ&Uacute;NG/SAI - C&aacute;c thầy c&ocirc; gi&aacute;o c&ograve;n hướng dẫn c&aacute;c em hướng suy nghĩ để giải đề, tổng hợp l&yacute; thuyết những c&acirc;u dễ bị sai; ghi ra c&aacute;ch l&agrave;m nhanh nhất để &ldquo;ăn điểm&rdquo;. &lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;/ul&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;https://lh4.googleusercontent.com/XZvYKJQfgn4sN8hkwbDRRC9l6Arcv0kYRObPHzfnyinVPU2QmSKnCfO4yfKAhGK46jYknv5vChqk2w3GeUnsmf2cqfGQysJEOzS4EYpeu18k-N_yP29UxMHu8cyladwSwJV6Hur7&quot; alt=&quot;3.png&quot; width=&quot;452&quot; height=&quot;301&quot; /&gt;&lt;/span&gt;&lt;/p&gt;  &lt;ul&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Kh&ocirc;ng chỉ thuần đề-v&agrave;-đề, s&aacute;ch Mega Luyện đề c&ograve;n tổng hợp kiến thức nền tảng v&agrave; l&yacute; thuyết h&oacute;a c&aacute;c kĩ năng l&agrave;m b&agrave;i &ldquo;si&ecirc;u nhanh - si&ecirc;u chuẩn&rdquo; d&agrave;nh ri&ecirc;ng cho c&aacute;c học sinh th&acirc;n thiết của Megabook.&lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;li dir=&quot;ltr&quot;&gt;  &lt;p dir=&quot;ltr&quot;&gt;&lt;span&gt;Thi thử Online, đăng nhập bằng m&atilde; code sau mỗi cuốn s&aacute;ch v&agrave; l&agrave;m theo hướng dẫn. Kh&ocirc;ng chỉ l&agrave;m đề, Mega Test c&ograve;n c&oacute; th&ocirc;ng số l&agrave;m b&agrave;i của từng học sinh. C&aacute;c em sẽ biết m&igrave;nh tốn trung b&igrave;nh bao nhi&ecirc;u thời gian l&agrave;m 1 c&acirc;u; Thống k&ecirc; chi tiết số c&acirc;u ho&agrave;n th&agrave;nh đ&uacute;ng; So s&aacute;nh với c&aacute;c bạn kh&aacute;c c&ugrave;ng l&agrave;m đề...&lt;/span&gt;&lt;/p&gt;  &lt;/li&gt;  &lt;/ul&gt;  &lt;h1 dir=&quot;ltr&quot;&gt;&lt;span&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;https://lh5.googleusercontent.com/6rwTkdEpHhC7N6db8tQtd0-OyWLLB6LNsZ5I2f1OeMiBu1lOr85AqfG3SDrXKiTPxNX1yYupsVgBtaIp5UcS-dwfo1u-JpO2q42a6A8_2ODmTw1OYnuxd8kT6RUabscdSi9SJCWm&qu