							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: medium; color: #ff6600;&quot;&gt;&lt;strong&gt;Lu&acirc;n L&yacute; Gi&aacute;o Khoa Thư&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Quốc văn gi&aacute;o khoa thư &lt;/strong&gt;v&agrave; &lt;strong&gt;Lu&acirc;n L&yacute; Gi&aacute;o Khoa Thư &lt;/strong&gt;l&agrave; hai bộ &lt;a href=&quot;http://tiki.vn/sach-giao-khoa-cac-cap/c2321&quot;&gt;s&aacute;ch gi&aacute;o khoa&lt;/a&gt; tiếng Việt được dạy song h&agrave;nh ở c&aacute;c trường Tiểu học Việt Nam trong suốt những thập ni&ecirc;n thuộc nửa đầu thế kỷ XX.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Nh&oacute;m bi&ecirc;n soạn s&aacute;ch gồm c&aacute;c &ocirc;ng&nbsp;&lt;strong&gt;Trần Trọng Kim&lt;/strong&gt;, &lt;strong&gt;Nguyễn Văn Ngọc&lt;/strong&gt;,&lt;strong&gt; Đặng Đ&igrave;nh Ph&uacute;c&lt;/strong&gt; v&agrave; &lt;strong&gt;Đỗ Thận&lt;/strong&gt; - đều l&agrave; những học giả, nh&agrave; văn, v&agrave; l&agrave; nh&agrave; gi&aacute;o dục nổi tiếng, đương thời rất được t&iacute;n nhiệm.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;C&ocirc;ng việc bi&ecirc;n soạn s&aacute;ch của họ c&ograve;n mang t&iacute;nh định hướng của một nền gi&aacute;o dục.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Việc dạy song h&agrave;nh hai bộ s&aacute;ch &lt;strong&gt;Quốc văn gi&aacute;o khoa thư &lt;/strong&gt;v&agrave; &lt;strong&gt;Lu&acirc;n L&yacute; Gi&aacute;o Khoa Thư &lt;/strong&gt;từ lớp Đồng ấu, lớp Dự bị đến lớp Sơ đẳng ở bậc Tiểu học cho thấy vấn đề lu&acirc;n l&yacute;, đạo đức v&agrave; c&ocirc;ng d&acirc;n gi&aacute;o dục kh&ocirc;ng chỉ l&agrave; một tiết học m&agrave; l&agrave; một nội dung lớn xuy&ecirc;n suốt qu&aacute; tr&igrave;nh ph&aacute;t triển tr&iacute; &oacute;c v&agrave; t&acirc;m hồn trẻ thơ&lt;span&gt;.&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Từ đầu thập ni&ecirc;n 1990 đến nay, bộ s&aacute;ch Quốc văn gi&aacute;o khoa thư v&agrave; Lu&acirc;n L&yacute; Gi&aacute;o Khoa Thư đ&atilde; được Nh&agrave; xuất bản Trẻ t&aacute;i bản nhiều lần v&agrave; được đ&ocirc;ng đảo bạn đọc nhiệt t&igrave;nh đ&oacute;n nhận.&lt;/span&gt;&lt;/p&gt;'), 
