					[ 
							'book_name' => 'Làng Báo Sài Gòn', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;L&agrave;ng B&aacute;o S&agrave;i G&ograve;n&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;L&agrave;ng b&aacute;o S&agrave;i G&ograve;n &lt;/strong&gt;t&aacute;i hiện một thời kỳ l&agrave;m b&aacute;o s&ocirc;i nổi của tr&iacute; thức S&agrave;i G&ograve;n v&agrave; Việt Nam, với những tờ b&aacute;o c&oacute; số phận chỉ v&agrave;i th&aacute;ng hay v&agrave;i số b&aacute;o, thậm ch&iacute; một số b&aacute;o duy nhất, trong bối cảnh bi thương m&agrave; h&agrave;o h&ugrave;ng của một d&acirc;n tộc bị mất nước nhưng quyết kh&ocirc;ng cam chịu l&agrave;m n&ocirc; lệ.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Nhận định&lt;/strong&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&quot;Cuốn s&aacute;ch l&agrave; một bi&ecirc;n ni&ecirc;n sử b&aacute;o ch&iacute; thực sự c&ocirc;ng phu, hấp dẫn.C&ocirc;ng tr&igrave;nh mi&ecirc;u tả sinh động của Philippe M. F. Peycam tạo ra một đ&oacute;ng g&oacute;p độc đ&aacute;o v&agrave;o lịch sử Việt Nam đương đại v&agrave; c&aacute;c nh&agrave; nghi&ecirc;n cứu về thuộc địa. Cho đến nay, chưa c&oacute; c&ocirc;ng tr&igrave;nh n&agrave;o như vậy bằng tiếng Anh. Những nghi&ecirc;n cứu trước đ&oacute; về b&aacute;o ch&iacute; Việt Nam tập trung v&agrave;o nhật b&aacute;o cộng sản La Lutte v&agrave; &iacute;t lấy mẫu một c&aacute;ch hệ thống về c&aacute;c ấn bản được người Ph&aacute;p ủng hộ. Peycam cho ch&uacute;ng ta ch&acirc;n dung về c&aacute;c nh&agrave; b&aacute;o, chủ b&uacute;t, bi&ecirc;n tập - những người, thường l&agrave; cung cấp t&agrave;i ch&iacute;nh để duy tr&igrave;, đưa c&aacute;c tờ b&aacute;o ra với c&ocirc;ng ch&uacute;ng để th&aacute;ch thức hiện trạng. &Ocirc;ng thận trọng sử dụng c&aacute;c b&aacute;o c&aacute;o của mật vụ Ph&aacute;p v&agrave; l&agrave;m sống lại những con người n&agrave;y c&ugrave;ng sự pha trộn kh&aacute;c thường của về chủ nghĩa l&yacute; tưởng Ph&aacute;p v&agrave; đạo đức giả thực d&acirc;n.&quot;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;(Sophie Quinn-judge, Temple University)&lt;/strong&gt;&lt;/p&gt;'), 
