					[ 
							'book_name' => 'Hơn Cả Tin Tức', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;Hơn Cả Tin Tức&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Cuốn s&aacute;ch l&agrave; một nghi&ecirc;n cứu độc đ&aacute;o, đ&ocirc;i khi c&oacute; t&iacute;nh ph&ecirc; ph&aacute;n về b&aacute;o ch&iacute; đương đại, cả tr&ecirc;n mạng lẫn ngo&agrave;i mạng internet, v&agrave; t&igrave;m thấy niềm hứng khởi cho một sự thấu hiểu hiệu quả v&agrave; đầy kh&aacute;t vọng về nghề b&aacute;o trong những v&iacute; dụ từ c&aacute;c b&agrave;i b&aacute;o v&agrave; blog thế kỷ 21, đồng thời trong những hiểu biết chọn lọc về nghề b&aacute;o thế kỷ 20 v&agrave; c&aacute;c t&aacute;c phẩm viết l&aacute;ch của Benjamin Franklin thế kỷ 18. Hầu hết nỗ lực xử l&yacute; cuộc khủng hoảng b&aacute;o ch&iacute; hiện nay nhấn mạnh v&agrave;o c&ocirc;ng nghệ. Stephens nhấn mạnh v&agrave;o tư duy v&agrave; nhu cầu tư duy lại về b&aacute;o ch&iacute; đ&atilde; từng l&agrave; v&agrave; c&oacute; thể trở th&agrave;nh c&ocirc;ng việc g&igrave;.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Nhận định&lt;/strong&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&quot;Quyển s&aacute;ch thu h&uacute;t n&agrave;y cho ch&uacute;ng ta biết b&aacute;o ch&iacute; phải thay đổi như thế n&agrave;o nhằm phục vụ thời đại v&agrave; c&ocirc;ng ch&uacute;ng tốt hơn. Stephens k&ecirc;u gọi sự diễn giải s&acirc;u sắc v&agrave; th&ocirc;ng minh, v&agrave; sự soi s&aacute;ng. Hơn cả tin tức cung cấp tất cả những điều đ&oacute; v&agrave; hơn thế. K&iacute;ch th&iacute;ch suy nghĩ, l&agrave; một cuốn s&aacute;ch th&uacute; vị để đọc.&quot;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;(Geneva Overholser, nghi&ecirc;n cứu sinh cao cấp)&lt;/strong&gt;&lt;/p&gt;'), 
