					[ 
							'book_name' => 'Đi Giữa Vô Thường', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;Đi giữa v&ocirc; thường l&agrave; cuốn s&aacute;ch tập hợp  những b&agrave;i viết trong tạp ch&iacute; Văn h&oacute;a Phật gi&aacute;o từ năm 2007 đến năm 2010  với nội dung xoay quanh c&aacute;c vấn đề li&ecirc;n quan đến những nguy&ecirc;n nh&acirc;n khiến  ch&uacute;ng ta khổ v&agrave; phương c&aacute;ch để đoạn diệt khổ v&agrave; đạt được ch&acirc;n hạnh  ph&uacute;c.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Cuốn s&aacute;ch gồm 3 phần :&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;em&gt;&lt;strong&gt;Phần 1: Khổ l&agrave; g&igrave;? Kh&ocirc;ng khổ l&agrave; g&igrave;? Thế n&agrave;o l&agrave; khổ đau, thế n&agrave;o l&agrave; hạnh ph&uacute;c?&lt;/strong&gt;&lt;/em&gt; Gi&uacute;p độc giả tiếp cận với kh&aacute;i niệm khổ đau v&agrave; hạnh ph&uacute;c trong Phật gi&aacute;o.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;em&gt;&lt;strong&gt;Phần 2: Nhận diện khổ đau&lt;/strong&gt;&lt;/em&gt;&lt;br /&gt; Gương mặt của khổ đau: s&acirc;n si, ghen tu&ocirc;ng, đố kị, nh&uacute;t nh&aacute;t, tự cao tự  đại, giận dữ, th&ugrave; hận, lo lắng&hellip; Nguy&ecirc;n nh&acirc;n của bất hạnh đau khổ l&agrave; do  kh&ocirc;ng c&oacute; l&ograve;ng tin, kh&ocirc;ng hổ thẹn, kh&ocirc;ng sợ h&atilde;i, kh&ocirc;ng tinh tấn, kh&ocirc;ng c&oacute;  tr&iacute; tuệ.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;em&gt;&lt;strong&gt;Phần 3: Đi giữa v&ocirc; thường&lt;/strong&gt;&lt;/em&gt;&lt;br /&gt; Phương c&aacute;ch để loại bỏ nỗi khổ, để đạt được ch&acirc;n hạnh ph&uacute;c, gương mặt của hạnh ph&uacute;c đ&iacute;ch thực.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Văn h&oacute;a Phật gi&aacute;o&lt;/strong&gt; l&agrave;  tạp ch&iacute; của tất cả những ai chấp nhận tư tưởng Phật gi&aacute;o, quyết t&acirc;m x&acirc;y  dựng một x&atilde; hội l&agrave;nh mạnh, hiền h&ograve;a v&agrave; bảo vệ, ph&aacute;t huy truyền thống của  d&acirc;n tộc.&lt;/p&gt;'), 
							'publish_date' => '2012-01-14', 
