					[ 
							'book_name' => 'Dạy Con Cách Tiêu Tiền', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;Dạy Con C&aacute;ch Ti&ecirc;u Tiền&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Brette McWhorter Sember&lt;/strong&gt; từng l&agrave; một luật sư v&agrave; l&agrave; t&aacute;c giả của 40 cuốn s&aacute;ch viết về c&aacute;ch gi&aacute;o dục con c&aacute;i. B&agrave; từng tham gia giảng dạy trực tuyến về c&aacute;ch sử dụng tiền bạc v&agrave; gi&aacute; trị của đồng tiền ở hai trường Đại học Noble v&agrave; barnes, đồng thời b&agrave; cũng l&agrave; t&aacute;c giả của cuốn s&aacute;ch &lt;strong&gt;Dạy con c&aacute;ch ti&ecirc;u tiền - Everything Kids&rsquo; Money Book&lt;/strong&gt;.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Dạy con c&aacute;ch ti&ecirc;u tiền &lt;/strong&gt;l&agrave; cuốn s&aacute;ch dạy trẻ c&aacute;ch sử dụng đồng tiền, kh&ocirc;ng bao giờ l&agrave; qu&aacute; sớm để dạy trẻ về gi&aacute; trị của đồng tiền bởi việc học về tiền, hiểu về tiền, biết kiếm tiền v&agrave; biết quản l&yacute; chi ti&ecirc;u hợp l&yacute; sẽ gi&uacute;p trẻ tự tin kiểm so&aacute;t t&agrave;i ch&iacute;nh của m&igrave;nh ngay từ khi c&ograve;n nhỏ. Tại sao con bạn chỉ biết ngửa tay xin tiền trong khi con người kh&aacute;c đ&atilde; biết c&aacute;ch kiếm tiền v&agrave; tại sao con bạn chỉ biết ti&ecirc;u x&agrave;i phung ph&iacute; m&agrave; chưa một lần từng nghĩ những đồng tiền con ti&ecirc;u được đổi lấy từ sự vất vả, cực nhọc của bố mẹ. Nếu con hiểu được như vậy th&igrave; chắc c&oacute; lẽ con đ&atilde; c&oacute; c&aacute;i nh&igrave;n v&agrave; c&aacute;ch suy nghĩ kh&aacute;c khi sử dụng đồng tiền, nhưng để con c&oacute; được những hiểu biết s&acirc;u sắc như vậy th&igrave; việc dạy cho con về những kiến thức ấy phải được bắt đầu ngay từ khi con c&ograve;n nhỏ.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span&gt;Ch&iacute;n chương trong cuốn s&aacute;ch sẽ l&agrave; ch&iacute;n b&agrave;i học cần thiết về tiền bạn cần dạy cho con để gi&uacute;p con hiểu được gi&aacute; trị của những đồng tiền m&agrave; con sử dụng h&agrave;ng ng&agrave;y, từ việc hiểu được gi&aacute; trị của đồng tiền sẽ gi&uacute;p con biết c&aacute;ch tiết kiệm tiền v&agrave; biết đầu tư tiền của m&igrave;nh một c&aacute;ch kh&ocirc;n kh&eacute;o để tiền đẻ ra tiền v&agrave; quan trọng hơn cả l&agrave; từ cuốn s&aacute;ch sẽ dạy cho c&aacute;c em c&aacute;ch chi ti&ecirc;u những đồng tiền m&igrave;nh c&oacute; một c&aacute;ch hợp l&yacute; để trở th&agrave;nh người trưởng th&agrave;nh ti&ecirc;u d&ugrave;ng th&ocirc;ng th&aacute;i trong tương lai. &lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Nhận định&lt;/strong&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&ldquo;Đ&acirc;y l&agrave; cuốn s&aacute;ch tuyệt vời để gi&uacute;p trẻ t&igrave;m hiểu về tiền v&agrave; c&aacute;ch sử dụng tiền, đồng thời gi&uacute;p trẻ hiểu được c&aacute;ch thức hoạt động của c&aacute;c ng&acirc;n h&agrave;ng cũng như c&aacute;ch sử dụng c&aacute;c loại thẻ t&iacute;n dụng&hellip;. Những nội dung trong cuốn s&aacute;ch l&agrave; những &yacute; tưởng tuyệt vời để gi&uacute;p trẻ biết c&aacute;ch tiết kiệm tiền v&agrave; quản l&yacute; chi ti&ecirc;u một c&aacute;ch hợp l&yacute; nhất. Ch&aacute;u trai t&ocirc;i cũng rất th&iacute;ch cuốn s&aacute;ch n&agrave;y&rdquo;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;(Oscar Somerset)&lt;/strong&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&ldquo;T&ocirc;i c&oacute; hai con g&aacute;i, một đứa 10 tuổi v&agrave; một đứa 8 tuổi, đ&acirc;y l&agrave; độ tuổi tuyệt vời để c&aacute;c con t&igrave;m hiểu về tiền. Trong v&ocirc; v&agrave;n những cuốn s&aacute;ch dạy trẻ về gi&aacute; trị của đồng tiền, t&ocirc;i đ&atilde; quyết định chọn mua cuốn s&aacute;ch n&agrave;y để dạy cho hai c&ocirc; con g&aacute;i của m&igrave;nh v&agrave; t&ocirc;i cảm thấy vui v&igrave; quyết định mua cuốn s&aacute;ch n&agrave;y của t&ocirc;i l&agrave; đ&uacute;ng đắn. Đ&acirc;y đ&uacute;ng l&agrave; một cuốn &lt;a href=&quot;http://tiki.vn/sach-hay-tiki-khuyen-doc&quot;&gt;s&aacute;ch hay&lt;/a&gt;&rdquo;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;(BassoonMomma)&lt;/strong&gt;&lt;/p&gt;'), 
