					[ 
							'book_name' => 'Có Một Con Đường Mòn Trên Biển Đông', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span&gt;C&oacute; Một Con Đường M&ograve;n Tr&ecirc;n Biển Đ&ocirc;ng&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Thi&ecirc;n k&yacute; sự về việc h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển đo&agrave;n t&agrave;u kh&ocirc;ng số, những con t&agrave;u b&iacute; mật vận chuyển vũ kh&iacute; từ miền Bắc v&agrave;o chi viện cho chiến trường miền Nam trong những năm kh&aacute;ng chiến chống Mỹ &aacute;c liệt nhất.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Con đường m&ograve;n tr&ecirc;n biển Đ&ocirc;ng m&agrave; c&aacute;c chiến sĩ hải qu&acirc;n c&ugrave;ng nh&acirc;n d&acirc;n d&agrave;y c&ocirc;ng gầy dựng l&agrave; con đường đầy gian nan v&agrave; nguy hiểm, nơi ghi dấu biết bao tấm gương chiến đấu quả cảm, bao hy sinh thầm lặng, bao cảnh đời &eacute;o le nghiệt ng&atilde;, v&agrave; cả những mối t&igrave;nh đẹp vượt qua kh&oacute;i lửa chiến tranh.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Tập s&aacute;ch gồm những nội dung ch&iacute;nh như sau:&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;C&ograve;n kh&ocirc;ng dấu vết một con đường?&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Mở đường&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Đi về hướng sao Bắc đẩu&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;T&agrave;u kh&ocirc;ng số&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Vũng R&ocirc;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Những tờ phiếu rời&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Biển v&agrave; bờ&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Thay h&igrave