					[ 
							'book_name' => 'Cẩm Nang Chăm Sóc Mẹ Và Bé Sau Khi Sinh', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;strong&gt;Cẩm Nang Chăm S&oacute;c Mẹ V&agrave; B&eacute; Sau Khi Sinh&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;strong&gt;***&lt;br /&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Những kiến thức về sức khỏe sinh sản v&agrave; nu&ocirc;i dậy con c&aacute;i v&ocirc; c&ugrave;ng cần thiết với c&aacute;c cặp vợ chồng trẻ hiện nay. Chắc hẳn c&aacute;c bậc l&agrave;m cha, l&agrave;m mẹ đều mong muốn m&igrave;nh sinh ra những em b&eacute; khỏe mạnh, lớn kh&ocirc;n, nhưng để biến mong muốn đ&oacute; th&agrave;nh hiện thực, ch&uacute;ng ta phải chuẩn bị v&agrave; l&agrave;m như thế n&agrave;o? C&aacute;c bạn đừng qu&aacute; lo lắng, nắm bắt được t&acirc;m l&yacute; đ&oacute;, ch&uacute;ng t&ocirc;i đ&atilde; bi&ecirc;n soạn, chỉnh l&yacute; v&agrave; cho ra đời bộ s&aacute;ch hữu &iacute;ch n&agrave;y, những kiến thức trong bộ s&aacute;ch sẽ kịp thời trang bị cho c&aacute;c bậc cha mẹ những kiến thức quan trọng từ giai đoạn khi người phụ nữ chuẩn bị mang thai, trong giai đoạn thai kỳ, sau khi sinh con, chăm s&oacute;c v&agrave; nu&ocirc;i d&acirc;ỵ con c&aacute;i... Bộ s&aacute;ch giống như l&agrave; một kim chỉ nam, một bộ B&aacute;ch khoa to&agrave;n thư về sinh sản v&agrave; nu&ocirc;i con m&agrave; ch&uacute;ng t&ocirc;i muốn gửi tặng tới tất cả c&aacute;c bậc l&agrave;m cha mẹ.&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Bộ s&aacute;ch được ho&agrave;n th&agrave;nh l&agrave; nhờ sự gi&uacute;p đỡ, cộng t&aacute;c của đội ngũ chuy&ecirc;n gia, b&aacute;c sĩ gi&agrave;u kinh nghiệm, dưới sự chủ bi&ecirc;n của t&aacute;c giả Ng&ocirc; Quang Tr&igrave;. &nbsp;&nbsp;Ng&ocirc; Quang Tr&igrave; l&agrave; chuy&ecirc;n gia nghi&ecirc;n cứu dinh dưỡng cho trẻ em thuộc Viện nghi&ecirc;n cứu Nhi khoa Bắc Kinh, Ủy vi&ecirc;n của Hiệp hội Khoa học Sức khỏe sinh sản Trung Quốc, Ph&oacute; Chủ nhiệm Ủy ban Dinh dưỡng cho trẻ thuộc Hiệp hội Khoa học Sức khỏe sinh sản Trung Quốc, Chủ bi&ecirc;n của &ldquo;Tạp ch&iacute; Nhi khoa Trung Quốc&rdquo;. Tốt nghiệp trường Đại học Y khoa Bắc Kinh năm 1958, theo đuổi chuy&ecirc;n ng&agrave;nh l&acirc;m s&agrave;ng nhi khoa, sức khỏe nhi đồng, nghi&ecirc;n cứu dinh dưỡng cho trẻ em&hellip; c&oacute; kinh nghiệm phong ph&uacute; v&agrave; l&acirc;u năm trong lĩnh vực chăm s&oacute;c sức khỏe trẻ em. Nhờ đ&oacute; m&agrave; bộ s&aacute;ch sau khi ra đời đ&atilde; nhận được sự đ&aacute;nh gi&aacute; cao về nội dung, h&igrave;nh thức của giới chuy&ecirc;n m&ocirc;n, b&aacute;o ch&iacute;...&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Bộ s&aacute;ch gồm13 tập: &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;365 ng&agrave;y chuẩn bị trước khi      mang thai&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Dinh dưỡng khoa học cho 40 tuần      mang thai&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Cẩm nang chăm s&oacute;c mẹ v&agrave; b&eacute; sau      khi sinh&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Dinh dưỡng khoa học cho b&eacute; từ      0-3 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Cẩm nang chăm s&oacute;c v&agrave; dạy trẻ từ      0-3 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Nu&ocirc;i con khoẻ mạnh từ 0-1 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Nu&ocirc;i con khoẻ mạnh từ 1-3 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;R&egrave;n tr&iacute; th&ocirc;ng minh cho con từ      0-1 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;R&egrave;n tr&iacute; th&ocirc;ng minh cho con từ      1-3 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Con bạn c&oacute; khỏe mạnh v&agrave; th&ocirc;ng      minh kh&ocirc;ng?&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Diệu kế nu&ocirc;i con khỏe mạnh v&agrave;      th&ocirc;ng minh từ 0-3 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;B&eacute; bệnh th&igrave; l&agrave;m thế n&agrave;o?&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Phương ph&aacute;p ph&ograve;ng- trị bệnh      thường gặp ở trẻ từ 0-3 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Tr&ograve; chơi ph&aacute;t triển tr&iacute; tuệ cho      trẻ từ 0-3 tuổi&lt;/em&gt; &lt;em&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;em&gt;Cẩm nang ph&aacute;t triển tiềm năng      tr&iacute; tuệ cho trẻ từ 3-6 tuổi&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;  &lt;ul style=&quot;text-align: justify;&quot;&gt;  &lt;/ul&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&nbsp;&lt;/p&gt;'), 
