					[ 
							'book_name' => 'Bây Giờ Mới Thấy', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;B&acirc;y Giờ Mới Thấy&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;B&acirc;y giờ mới thấy nghĩa l&agrave; bấy l&acirc;u nay chưa từng thấy. Bấy l&acirc;u nay c&oacute; thể m&igrave;nh đ&atilde; đi t&igrave;m nhưng chưa từng thấy. V&agrave; c&oacute; thể l&agrave; b&acirc;y giờ m&igrave;nh kh&ocirc;ng đi t&igrave;m nữa, cho n&ecirc;n m&igrave;nh mới c&oacute; cơ hội thấy. Thấy c&aacute;i g&igrave; v&agrave; m&igrave;nh đ&atilde; đi t&igrave;m c&aacute;i g&igrave;?&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;M&igrave;nh đ&atilde; đi t&igrave;m c&aacute;i g&igrave; nhỉ? M&igrave;nh đ&atilde; đi t&igrave;m ai? C&oacute; thể m&igrave;nh đ&atilde; đi t&igrave;m ch&iacute;nh m&igrave;nh, để biết m&igrave;nh l&agrave; ai? Ai l&agrave; người đang niệm Bụt ? Ai l&agrave; người đang thực tập qu&aacute;n chiếu, ai l&agrave; người đang tu?&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Ng&agrave;y xưa c&aacute;ch đ&acirc;y khoảng 50 năm, tại ch&ugrave;a Bảo Li&ecirc;n, đảo Lantau, tr&ecirc;n v&aacute;ch nh&agrave; c&oacute; viết một b&agrave;i kệ như sau:&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Hữu độ tức phi tịnh&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Ng&ocirc;n thuy&ecirc;n h&agrave; sở vi?&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Phật thuyết nguy&ecirc;n v&ocirc; ng&atilde;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Thiền sư vấn thị th&ugrave;y&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Hễ c&oacute; một c&otilde;i th&igrave; c&otilde;i ấy kh&ocirc;ng thể l&agrave; c&otilde;i tịnh được. C&oacute; c&otilde;i th&igrave; c&oacute; sự sống. C&oacute; sự sống nghĩa l&agrave; c&oacute; ăn uống, c&oacute; b&agrave;i tiết. C&oacute; thiền đường m&agrave; cũng c&oacute; cầu ti&ecirc;u. C&oacute; cầu ti&ecirc;u th&igrave; kh&ocirc;ng phải l&agrave; tịnh rồi. Vậy th&igrave; c&otilde;i ấy, c&otilde;i tịnh độ của Bụt A Di Đ&agrave; m&agrave; những người tịn độ đang cầu về c&oacute; thực sự l&agrave; một c&otilde;i tịnh hay kh&ocirc;ng?&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Ng&ocirc;n thuy&ecirc;n h&agrave; sở vi c&oacute; nghĩa l&agrave; ng&ocirc;n thuyết d&ugrave;ng để l&agrave;m g&igrave;? Hễ mở miệng l&agrave; mắc quai. Chỉ cần n&oacute;i ra hai tiếng tịnh độ l&agrave; đ&atilde; bị kẹt. Hễ c&oacute; độ l&agrave; kh&ocirc;ng thể c&oacute; tịnh.&lt;br /&gt;Hữu độ tức phi thịnh nghĩa l&agrave; thế.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Nếu điều Bụt dạy về v&ocirc; ng&atilde; l&agrave; đ&uacute;ng th&igrave; thiền sư l&agrave; ai? Theo Tịnh độ th&igrave; bị kẹt đ&atilde; đ&agrave;nh m&agrave; theo Thiền cũng bị kẹt. Vị thiền sư m&agrave; m&igrave;nh đang tham vấn c&oacute; phải l&agrave; một c&aacute;i ng&atilde; kh&ocirc;ng? M&igrave;nh l&agrave; ai, vị thiền sư l&agrave; ai? Ai l&agrave; người niệm Bụt, ai l&agrave; người đang tham vấn với vị thiền sư?&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;(Th&iacute;ch Nhất Hạnh)&lt;/strong&gt;&lt;/p&gt;'), 
