					[ 
							'book_name' => 'Ba Trụ Thiền', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;Ba Trụ Thiền&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Ba cột trụ của Thiền, đ&oacute; l&agrave; Gi&aacute;o l&yacute;, Tu tập, v&agrave; Gi&aacute;c ngộ. L&atilde;o sư Philip Kapleau, người đ&oacute;ng vai tr&ograve; ch&iacute;nh trong việc&nbsp;đưa Phật gi&aacute;o Thiền về ch&acirc;u Mỹ, đ&atilde; tr&igrave;nh b&agrave;y trong cuốn s&aacute;ch n&agrave;y một tổng quan h&agrave;m s&uacute;c về lịch sử v&agrave; quy luật của&nbsp;Phật gi&aacute;o Thiền. T&aacute;c phẩm kinh điển c&ograve;n được cập nhật v&agrave; hiệu chỉnh, được bổ sung c&aacute;c ảnh tư liệu mới.&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Nhận định&lt;/strong&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&ldquo;Hơn ba chục năm qua, Ba Trụ Thiền của L&atilde;o sư Kapleau l&agrave; nguồn suối gi&aacute;o l&yacute; Thiền cho những người tu tập tọa thiền&nbsp;ở phương T&acirc;y, m&agrave; ng&agrave;y nay n&oacute; vẫn c&ograve;n sinh động v&agrave; tươi m&aacute;t như khi được xuất bản lần đầu ti&ecirc;n. Cuốn s&aacute;ch thực sựđứng v&agrave;o h&agrave;ng những t&aacute;c phẩm kinh điển phi thời gian của Phật gi&aacute;o Thiền.&rdquo;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;(L&atilde;o sư John Daido Lori, Viện trưởng Zen Mountain Monastery)&lt;/strong&gt;&lt;/p&gt;'), 
