					[ 
							'book_name' => '7 Kì Quan Thế Giới', 
							'description' => html_entity_decode('&lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;7 KỲ QUAN THẾ GIỚI&lt;/strong&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;***&lt;/strong&gt;&lt;/p&gt;  &lt;p&gt;&lt;img style=&quot;display: block; margin-left: auto; margin-right: auto;&quot; src=&quot;https://img.tiki.vn/media/sach/2012/thang2/7-ki-quan-the-gioi.jpg&quot; alt=&quot;&quot; width=&quot;300&quot; /&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;strong&gt; &lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;em&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Đại kim tự th&aacute;p được x&acirc;y dựng ra sao?&lt;/span&gt;&lt;/em&gt;&lt;br /&gt;&lt;br /&gt;&lt;em&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Tượng thần Zeus khổng lồ như thế n&agrave;o?&lt;/span&gt;&lt;/em&gt;&lt;br /&gt;&lt;br /&gt;&lt;em&gt;&lt;span style=&quot;font-size: small;&quot;&gt;V&igrave; sao Vườn treo Babylon được coi l&agrave; thi&ecirc;n đường hạ giới?&lt;/span&gt;&lt;/em&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Bằng những mi&ecirc;u tả cặn kẽ c&ugrave;ng h&igrave;nh ảnh minh họa chi tiết, cuốn s&aacute;ch n&agrave;y sẽ gi&uacute;p bạn trả lời những c&acirc;u hỏi ấy th&ocirc;ng qua việc giới thiệu to&agrave;n cảnh 7 k&igrave; quan thế giới cổ đại, từ ho&agrave;n cảnh ra đời cho đến kĩ thuật x&acirc;y dựng của người xưa. Ngo&agrave;i ra, bạn c&ograve;n được kh&aacute;m ph&aacute; h&agrave;ng loạt c&aacute;c c&ocirc;ng tr&igrave;nh nổi tiếng kh&aacute;c c&ugrave;ng những k&igrave; quan thi&ecirc;n nhi&ecirc;n k&igrave; vĩ v&agrave; những th&agrave;nh tựu vượt bậc của nh&acirc;n loại.&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Cuốn s&aacute;ch được tr&igrave;nh b&agrave;y khoa học với những ch&uacute; giải tỉ mỉ, ch&iacute;nh x&aacute;c c&ugrave;ng với h&agrave;ng trăm minh họa sinh động, chi tiết. Đ&acirc;y l&agrave; điểm hấp dẫn khiến bạn kh&ocirc;ng thể bỏ qua cuốn s&aacute;ch n&agrave;y.&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=&quot;background-color: #ffffff; font-size: small;&quot;&gt;H&atilde;y c&ugrave;ng kh&aacute;m ph&aacute; thế giới k&igrave; vĩ với 7 k&igrave; quan thế giới!&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;background-color: #00ccff; font-size: small;&quot;&gt;&lt;strong&gt;Dự kiến ph&aacute;t h&agrave;nh 03/2012&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&lt;strong&gt;Mời c&aacute;c bạn đ&oacute;n đọc!&lt;/strong&gt;&lt;strong&gt;&lt;br /&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;'), 
							'publish_date' => '2012-03-14', 
							'author_id' => null, 
